import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext'; // Adjust the path as necessary
import styles from '../../styles/Navbar.module.css'; // Import the CSS module

const Navbar = () => {
  const { user, logout } = useAuth(); // Access user and logout function from Auth context
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // Call the logout function from the Auth context
    navigate('/login'); // Redirect to the login page
  };

  return (
    <nav className={`${styles.navbar} ${styles.collapse}`}>
      <div className={styles.navbar__logo}>
        <NavLink to="/">Cocktails App</NavLink>
      </div>
      <ul className={styles.navbar__links}>
        <li><NavLink to="/" activeclassname={styles.active}>Dashboard</NavLink></li>
        <li><NavLink to="/recipes" activeclassname={styles.active}>Recipes</NavLink></li>
        <li><NavLink to="/menu/create" activeclassname={styles.active}>Create Menu</NavLink></li>
        <li><NavLink to="/profile" activeclassname={styles.active}>Profile</NavLink></li>
      </ul>
      <div className={styles.navbar__auth}>
        {user ? (
          <button onClick={handleLogout} className={styles.logoutButton}>Logout</button> // Show Logout button if user is logged in
        ) : (
          <NavLink to="/login" activeclassname={styles.active}>Login</NavLink> // Show Login link if user is not logged in
        )}
      </div>
    </nav>
  );
};

export default Navbar;
