import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import mockCocktails from '../../data/mockCocktails'; // Import the mock cocktail data
import styles from '../../styles/recipelist.module.css'; // Import CSS module for styling

const RecipeList = () => {
  const [filter, setFilter] = useState('');
  const [complexity, setComplexity] = useState('');

  // Filter cocktails based on style and complexity
  const filteredCocktails = mockCocktails.filter(cocktail => {
    const matchesStyle = filter ? cocktail.style === filter : true;
    const matchesComplexity = complexity ? cocktail.complexityLevel === complexity : true;
    return matchesStyle && matchesComplexity;
  });

  // Get unique styles and complexities for filter options
  const stylesOptions = [...new Set(mockCocktails.map(cocktail => cocktail.style))];
  const complexityOptions = [...new Set(mockCocktails.map(cocktail => cocktail.complexityLevel))];

  return (
    <div className={styles.recipeList}>
      <h1>Cocktail Recipes</h1>

      {/* Filter Options */}
      <div className={styles.filters}>
        <label>
          Filter by Style:
          <select onChange={(e) => setFilter(e.target.value)} value={filter}>
            <option value="">All</option>
            {stylesOptions.map(style => (
              <option key={style} value={style}>{style}</option>
            ))}
          </select>
        </label>

        <label>
          Filter by Complexity:
          <select onChange={(e) => setComplexity(e.target.value)} value={complexity}>
            <option value="">All</option>
            {complexityOptions.map(level => (
              <option key={level} value={level}>{level}</option>
            ))}
          </select>
        </label>
      </div>

      {/* Cocktail List */}
      <div className={styles.recipeGrid}>
        {filteredCocktails.map(cocktail => (
          <div key={cocktail.id} className={styles.recipeCard}>
            <Link to={`/recipes/${cocktail.id}`} className={styles.recipeLink}>
              <h2>{cocktail.name}</h2>
              <p><strong>Style:</strong> {cocktail.style}</p>
              <p><strong>Complexity:</strong> {cocktail.complexityLevel}</p>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecipeList;
