import React, { useState } from "react";
import profilePic from "../../assets/images/profile.jpg";
import barPic from "../../assets/images/bar.png";
import styles from "../../styles/profile.module.css";
import { useAuth } from "../../context/AuthContext"; // Adjust the path as necessary
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import mockCocktails from "../../data/mockCocktails"; // Import the mock cocktail data

const ProfilePage = () => {
  const { user } = useAuth(); // Get the user from the Auth context

  const [showEmpty, setShowEmpty] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [showDeletedMessage, setShowDeletedMessage] = useState(false);

  // Function to handle showing the confirmation dialog
  const handleDeleteClick = () => {
    console.log("are we here? why?");

    if (localStorage.length === 0) {
      setShowEmpty(true);
      setTimeout(() => {
        setShowEmpty(false);
      }, 2000);
    } else {
      setShowConfirmation(true);
    }
  };

  // Function to handle confirming deletion
  const confirmDelete = () => {
    localStorage.clear();
    setShowConfirmation(false); // Hide confirmation after deletion
    setShowDeletedMessage(true); // Show "Menus deleted" message

    // Set a timer to hide the deleted message after 1 second
    setTimeout(() => {
      setShowDeletedMessage(false);
    }, 2000);
  };

  // Function to handle canceling the deletion
  const cancelDelete = () => {
    setShowConfirmation(false);
  };

  return (
    <div className={styles.wrapper}>
      <div className={styles.section}>
        <div className={styles.imageContainer}>
          <img className={styles.image} src={profilePic} alt="Profile" />
        </div>
        <h1>{user.fullName}</h1>
        <h2>{user.role}</h2>
        <h3>{user.menusCreated} menus created</h3>
        <h3>Last logged in: {user.lastLogin}</h3>
      </div>
      <div className={styles.section}>
        <div className={styles.imageContainer}>
          <img className={styles.image} src={barPic} alt="Bar" />
        </div>
        <h1>{user.bar}</h1>
        <h2>{user.barAddress}</h2>
        <h3
          onClick={() => window.open(user.fbPage)}
          className={styles.linkButton}
        >
          FB page
        </h3>
        <h3
          onClick={() => window.open(user.instaPage)}
          className={styles.linkButton}
        >
          Insta page
        </h3>
      </div>
      <div className={styles.section}></div>

      <div className={styles.section}>
        <h1>Actions</h1>
        <div className={styles.buttonContainer}>
                  <button
          className={`${styles.deleteButton} ${
            showConfirmation ? styles.clicked : ""
          }`}
          onClick={handleDeleteClick}
          >
          <FontAwesomeIcon
            className={`${
              showConfirmation ? styles.deleteIconclicked : styles.deleteIcon
            }`}
            icon={faTrash}
            />
          Delete Menus
        </button>
        {/* Conditional rendering for the confirmation dialog */}
        {showConfirmation && (
          <div className={styles.confirmationDialog}>
            <p>Are you sure?</p>
            <button className={styles.confirmButton} onClick={confirmDelete}>
              Yes
            </button>
            <button className={styles.cancelButton} onClick={cancelDelete}>
              No
            </button>
          </div>
        )}
        {/* Conditional rendering for the "Menus deleted" message */}
        {showDeletedMessage && (
          <div className={styles.deletedMessage}>
            <p>Menus deleted</p>
          </div>
        )}
        {showEmpty && (
          <div className={styles.deletedMessage}>
            <p>You do not have any menus!</p>
          </div>
        )}
        </div>
      </div>

    </div>
  );
};

export default ProfilePage;
