import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable"; // For table functionality
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDownload } from "@fortawesome/free-solid-svg-icons"; // Import the download icon
import styles from "../../styles/ViewMenu.module.css";

const ViewMenu = () => {
  const { id } = useParams();
  const [menu, setMenu] = useState(null);

  useEffect(() => {
    // Retrieve menus from localStorage and find the one matching the id
    const savedMenus = JSON.parse(localStorage.getItem("menus")) || [];
    const foundMenu = savedMenus.find((menu) => menu.id === parseInt(id));
    setMenu(foundMenu);
  }, [id]);

  const handleDownloadPDF = () => {
    if (!menu) return;

    const doc = new jsPDF();
    const date = new Date().toLocaleDateString();

    // Set text properties
    const titleFontSize = 28;
    const headerFontSize = 26;
    const textFontSize = 16;
    const cocktailFontSize = 19; // Increased font size for cocktail names

    // Set the background color to black
    doc.setFillColor(0, 0, 0); // Black background
    doc.rect(0, 0, doc.internal.pageSize.getWidth(), doc.internal.pageSize.getHeight(), 'F');

    // Header with Bar Name
    doc.setFontSize(titleFontSize);
    doc.setTextColor(255, 255, 255); // White text
    doc.text("The Cocktail Lounge", 105, 20, null, null, "center");

    // Menu Title
    doc.setFontSize(headerFontSize);
    doc.text(menu.title, 105, 40, null, null, "center");

    // Date
    doc.setFontSize(textFontSize);
    doc.text(`Downloaded on: ${date}`, 105, 50, null, null, "center");

    // Cocktails Table
    const cocktails = menu.cocktails.map((cocktail) => [
      cocktail.name,
      `$${cocktail.price.toFixed(2)}`,
    ]);

    // AutoTable for cocktails
    doc.autoTable({
      head: [["Cocktail", "Price"]],
      body: cocktails,
      startY: 60,
      margin: { left: 20, right: 20 },
      theme: "plain", // Use plain theme for a minimalistic look
      styles: {
        fontSize: cocktailFontSize, // Increased font size for cocktail names
        cellPadding: 8,
        halign: 'center', // Center align text in the cells
        textColor: [255, 255, 255], // White text color
        fillColor: [0, 0, 0], // Black background for the table
      },
      headStyles: {
        textColor: [255, 193, 7], // White text for headers
        fontSize: 14,
        halign: 'center', // Center align headers
      },
      alternateRowStyles: {
        fillColor: [0, 0, 0], // Black background for alternating rows
      },
    });

    // Footer
    doc.setFontSize(textFontSize);
    doc.text(
      "Thank you for creating a menu using our app!",
      105,
      doc.internal.pageSize.height - 10,
      null,
      null,
      "center"
    );

    // Save the PDF
    doc.save(`${menu.title}_menu.pdf`);
  };



  if (!menu) {
    return <p>Menu not found</p>;
  }

  return (
    <div className={styles.wrapper}>
      <div className={styles.viewMenu}>
        <div className={styles.menuTitle}>
          <h3>The</h3>
          <h1>{menu.title}</h1>
          <h3>Menu</h3>
        </div>
        <ul>
          {menu.cocktails.map((cocktail) => (
            <li key={cocktail.id}>
              <p>{cocktail.name}</p>
              <p>€{cocktail.price}</p>
            </li>
          ))}
        </ul>
        {/* Download PDF button with Font Awesome icon */}
        <div className={styles.buttonContainer}>
          <p>Download PDF</p>
          <button onClick={handleDownloadPDF} className={styles.downloadButton}>
            <FontAwesomeIcon icon={faDownload} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ViewMenu;
