import React from "react";
import { useParams } from "react-router-dom";
import mockCocktails from "../../data/mockCocktails"; // Import mock cocktail data
import styles from "../../styles/recipedetail.module.css"; // Import CSS module for styling

const RecipeDetail = () => {
  const { id } = useParams();
  const cocktail = mockCocktails.find((cocktail) => cocktail.id === Number(id));

  const isActive = (index) => {
    if (cocktail.complexityLevel.toLowerCase() === 'easy' && index === 0) return true;
    if (cocktail.complexityLevel.toLowerCase() === 'medium' && index <= 1) return true;
    if (cocktail.complexityLevel.toLowerCase() === 'hard') return true;
    return false;
  };


  if (!cocktail) {
    return <div className={styles.error}>Cocktail not found!</div>;
  }

  return (
    <div className={styles.recipeDetail}>
      <h1>{cocktail.name}</h1>
      <div className={styles.metaData}>
        <h3>
          Style: <span>{cocktail.style}</span>
        </h3>
        <h3>
          Complexity: <span>{cocktail.complexityLevel}</span>
        </h3>
        <div className={styles.barContainer}>
          <div className={`${styles.bar} ${isActive(0) ? styles.barActive : ''}`}></div>
          <div className={`${styles.bar} ${isActive(1) ? styles.barActive : ''}`}></div>
          <div className={`${styles.bar} ${isActive(2) ? styles.barActive : ''}`}></div>
        </div>
      </div>
      <h4>Ingredients:</h4>
      <ul>
        {cocktail.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient}</li>
        ))}
      </ul>
      <h4>Recipe:</h4>
      <p>{cocktail.recipe}</p>
    </div>
  );
};

export default RecipeDetail;
