import React from 'react';
import { useParams } from 'react-router-dom';
import mockCocktails from '../../data/mockCocktails'; // Import mock cocktail data
import styles from '../../styles/recipedetail.module.css'; // Import CSS module for styling
import '../../styles/variables.css'; // Import global variables

const RecipeDetail = () => {
  const { id } = useParams();
  const cocktail = mockCocktails.find((cocktail) => cocktail.id === Number(id));

  // Determine the number of active bars based on complexity level
  const getActiveBars = (complexityLevel) => {
    switch (complexityLevel.toLowerCase()) {
      case 'easy':
        return 1;
      case 'medium':
        return 2;
      case 'hard':
        return 3;
      default:
        return 0;
    }
  };

  if (!cocktail) {
    return <div className={styles.error}>Cocktail not found!</div>;
  }

  return (
    <div className={styles.recipeDetail}>
      <h1>{cocktail.name}</h1>
      <div className={styles.metaData}>
        <h2>Style: <span>{cocktail.style}</span></h2>
        <h3>Complexity: <span>{cocktail.complexityLevel}</span></h3>
        <div className={styles.barContainer}>
          {[...Array(3)].map((_, index) => (
            <div
              key={index}
              className={`${styles.bar} ${
                index < getActiveBars(cocktail.complexityLevel) ? styles.active : ''
              }`}
            ></div>
          ))}
        </div>
      </div>
      <h4>Ingredients:</h4>
      <ul>
        {cocktail.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient}</li>
        ))}
      </ul>
      <h4>Recipe:</h4>
      <p>{cocktail.recipe}</p>
    </div>
  );
};

export default RecipeDetail;
