import React from 'react';
import { useAuth } from '../../context/AuthContext'; // Adjust the path as necessary
import styles from '../../styles/profile.module.css'; // Import the CSS module

const Profile = () => {
  const { user } = useAuth(); // Get the user from the Auth context

  // Example of user data structure; adjust based on your actual data
  const userInfo = {
    username: user?.username || 'Guest',
    fullName: user?.fullName || 'Full Name',
    role: user?.role || 'User',
    bar: user?.bar || 'Bar Name',
    // Add more fields as necessary
  };

  return (
    <div className={styles.profile}>
      <h1>User Profile</h1>
      <div className={styles.profile__info}>
        <p><strong>Username:</strong> {userInfo.username}</p>
        <p><strong>Full Name:</strong> {userInfo.fullName}</p>
        <p><strong>Role:</strong> {userInfo.role}</p>
        <p><strong>Bar:</strong> {userInfo.bar}</p>
        {/* Add more user info as necessary */}
      </div>
      <div className={styles.profile__actions}>
        <button className={styles.editButton}>Edit Profile</button>
        {/* Implement edit functionality if needed */}
      </div>
    </div>
  );
};

export default Profile;
