// src/pages/dashboard/Dashboard.jsx

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import SearchBar from "../../components/ui/SearchBar";
import styles from "../../styles/Dashboard.module.css";

const Dashboard = () => {
  const [menus, setMenus] = useState([]);

  useEffect(() => {
    // Retrieve menus from localStorage on component mount
    const savedMenus = JSON.parse(localStorage.getItem("menus")) || [];
    setMenus(savedMenus);
  }, []);

  return (
    <div className={styles.dashboard}>
      <h1>Dashboard</h1>
      {/* <SearchBar placeholder="Search for menus..." onSearch={() => {}} /> */}
      <div className={styles.menusList}>
        <h2>Your Menus</h2>
        {menus.length > 0 ? (
          <div className={styles.menuItemsContainer}>
            <ul>
              {menus.map((menu) => (
                <li key={menu.id}>
                  <Link to={`/viewmenu/${menu.id}`} className={styles.menuLink}>
                    {menu.title}
                  </Link>
                </li>
              ))}
            </ul>{" "}
          </div>
        ) : (
          <p>No menus created yet.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
