import React from "react";
import profilePic from "../../assets/images/profile.jpg";
import barPic from "../../assets/images/bar.png";
import styles from "../../styles/profile.module.css";
import { useAuth } from "../../context/AuthContext"; // Adjust the path as necessary
import mockCocktails from "../../data/mockCocktails"; // Import the mock cocktail data
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons"; // Import the download icon

const ProfilePage = () => {
  const deleteMenus = () => {
    localStorage.clear();
  };
  const { user } = useAuth(); // Get the user from the Auth context
  return (
    <div className={styles.wrapper}>
      <div className={styles.section}>
        <div className={styles.imageContainer}>
          <img className={styles.image} src={profilePic} />
        </div>
        <h1>{user.fullName}</h1>
        <h2>{user.role}</h2>
        <h3>{user.menusCreated} menus created</h3>
        <h3>Last logged in: {user.lastLogin}</h3>
      </div>
      <div className={styles.section}>
        <div className={styles.imageContainer}>
          <img className={styles.image} src={barPic} />
        </div>
        <h1>{user.bar}</h1>
        <h2>{user.barAddress}</h2>
      </div>
      <div className={styles.section}>
        <h1>actions section</h1>
        <button onClick={deleteMenus}>
          {" "}
          <FontAwesomeIcon icon={faTrash} />
        </button>
      </div>
    </div>
  );
};

export default ProfilePage;
