import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable"; // For table functionality
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDownload } from "@fortawesome/free-solid-svg-icons"; // Import the download icon
import styles from "../../styles/ViewMenu.module.css";

const ViewMenu = () => {
  const { id } = useParams();
  const [menu, setMenu] = useState(null);

  useEffect(() => {
    // Retrieve menus from localStorage and find the one matching the id
    const savedMenus = JSON.parse(localStorage.getItem("menus")) || [];
    const foundMenu = savedMenus.find((menu) => menu.id === parseInt(id));
    setMenu(foundMenu);
  }, [id]);

  const handleDownloadPDF = () => {
    if (!menu) return;

    const doc = new jsPDF();
    const date = new Date().toLocaleDateString();

    // Title
    doc.setFontSize(22);
    doc.text(menu.title, 105, 20, null, null, "center");

    // Date
    doc.setFontSize(12);
    doc.text(`Downloaded on: ${date}`, 105, 30, null, null, "center");

    // Cocktails Table
    const cocktails = menu.cocktails.map((cocktail) => [
      cocktail.name,
      `$${cocktail.price.toFixed(2)}`,
    ]);

    doc.autoTable({
      head: [["Cocktail", "Price"]],
      body: cocktails,
      startY: 40,
      margin: { left: 20, right: 20 },
      theme: "grid",
      styles: { fontSize: 12, cellPadding: 4 },
    });

    // Footer
    doc.setFontSize(10);
    doc.text(
      "Thank you for creating a menu using our app!",
      105,
      doc.internal.pageSize.height - 10,
      null,
      null,
      "center"
    );

    // Save the PDF
    doc.save(`${menu.title}_menu.pdf`);
  };

  if (!menu) {
    return <p>Menu not found</p>;
  }

  return (
    <div className={styles.viewMenu}>
      <h3>The</h3>
      <h1>{menu.title}</h1>
      <h3>Menu</h3>
      <ul>
        {menu.cocktails.map((cocktail) => (
          <li key={cocktail.id}>
            <p>{cocktail.name}</p>
            <p>â‚¬{cocktail.price}</p>
          </li>
        ))}
      </ul>
      {/* Download PDF button with Font Awesome icon */}
      <button onClick={handleDownloadPDF} className={styles.downloadButton}>
        <FontAwesomeIcon icon={faDownload} />
      </button>
    </div>
  );
};

export default ViewMenu;
